import React, {useEffect, useState} from 'react';
import "./style.css";
import logo from "../../assets/logo.svg";

import {Link} from "react-router-dom";
import {FiArrowLeft} from "react-icons/fi";
import {Map, TileLayer, Marker} from 'react-leaflet'
import api from "../../services/api";
import axios from 'axios';
//estado para array ou objeto, obrigatoriamente temos que infrmar o type do object

interface Item{
    id:number;
    title:string;
    image_url:string; 
}

interface IBGEUFResponse{
    sigla: string;
}

const CreatePoint = () =>{

    const [items, setItems] = useState<Item[]>([]);
    const [ufs, setUfs] = useState<string[]>([]);

    const[selectedUf, setSelectedUf] = useState('0');


    useEffect(() => {
        api.get('items').then(response => {
            setItems(response.data);
        })

    },   []);

    useEffect(() =>{
        axios.get<IBGEUFResponse[]>('https://servicodados.ibge.gov.br/api/v1/localidades/estados').then(response => {
            const ufInitials = response.data.map(uf => uf.sigla); 

            setUfs(ufInitials);
        });
    } ,[]);

    useEffect(()=>{

    }, []);

    function handleSelectedUf(){
        console.log("oi")
    }

return (
    <div id="page-create-point">
        <header>
            <img src={logo} alt="ecoleta"></img>
            <Link to="/">
                <FiArrowLeft/>
                Voltar Para Home

            </Link>
        </header>


        <form>

    <h1>  Cadastro do Ponto de Coleta</h1>

    <fieldset>
        <legend>
            <h2>Dados</h2>
        </legend>

        <div className="field">
            <label htmlFor="name">Nome da Entidade</label>
            <input
            type="text"
            name="name"
            id="name"
            />
        </div>

        <div className="field-group">
        <div className="field">
            <label htmlFor="email">Email</label>
            <input
            type="email"
            name="email"
            id="email"
            />
        </div>
        <div className="field">
            <label htmlFor="whatsapp">Whatsapp</label>
            <input
            type="text"
            name="whatsapp"
            id="whatsapp"
            />
        </div>

        </div>
    </fieldset>



    <fieldset>
        <legend>
            <h2>Endereço</h2>
            <span>Selecione o Endereço no Mapa</span>
        </legend>

        <Map center={[ -22.866911, -43.4443247,]} zoom={15}> 
            <TileLayer 
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"/>

          <Marker position={[ -22.866911, -43.4443247,]}/>
        </Map>

    <div className="field-group">
        <div className="field">
            <label htmlFor="uf">Estado</label>
            <select name="uf" id="uf" onChange={handleSelectedUf}>
            <option value="0">Selecione uma UF</option>
            {ufs.map(uf =>(
                <option key={uf} value={uf}>{uf}</option>
            ))}
            </select>
        </div>
    <div className="field">
            <label htmlFor="city">Cidade</label>
            <select name="city" id="city">
            <option value="0">Selecione uma Cidade</option>
            </select>
     </div>
    </div>

    </fieldset>




    <fieldset>
        <legend>
            <h2>Itens de Coleta</h2>
            <span>Selecione um ou mais itens abaixo </span>
        </legend>
        <ul className="items-grid">
            {items.map(item => (
            <li key= {item.id}>
                <img src={item.image_url} alt={item.title}/>
                <span>{item.title}</span> 
            </li> 
            ))}
        </ul>
    </fieldset>

    <button type="submit">
        Cadastrar Ponto de Coleta
    </button>

        </form>
    </div>
)
};

export default CreatePoint;
